import React from 'react';
import { data } from '../../utils/data.js';

export const Ptw = React.createContext();
let initPtwState = {
    ...data,
}

let reducer = (ptwState,action) => {
    switch (action.type) {
        case "PTW_CLOSE":
            return {ptw:[...ptwState.ptw, {...ptwState.ptw[action.ptwId],valid: !ptwState.ptw[action.ptwId].valid } ].remove(action.ptwId)}
        default:
            throw new Error();
    }
}
export default function PtwContext (props) {
    let [ptwState , dispatch] = React.useReducer(reducer,initPtwState);
    let globalPtwState = {
        ptwState,
        dispatch
    }

    return (
        <Ptw.Provider value={globalPtwState}>
            {props.children}
        </Ptw.Provider>
    )
}
