import React from 'react';
import SearchBar from '../SearchBar/SearchBar';
import {Box, Input, Switch, useTheme} from '@mui/material';
import { ColorModeContext, tokens } from '../theme/theme';


export default function Navbar () {
    const theme = useTheme();
    const colors = tokens(theme.palette.mode);
    let themeToggle = React.useContext(ColorModeContext);

    React.useEffect(
        ()=> {
            let toggleThumb = document.getElementsByClassName("MuiSwitch-thumb");
            console.log(toggleThumb)
        }
    )

    return (
        <>
            <Box 
            display={"flex"}
            width = {"100hv"}
            height = {"3rem"}
            justifyContent = {"space-between"}
            alignItems = {"center"}
            sx = {{
                backgroundColor: colors.primary[500]
            }}>
                <Input
                id = "search"

                sx = {{
                    marginLeft: "10px",
                    width: "18rem",
                }}        
                />

                <Box>
                    <Switch color = "warning" id="dark" onChange={
                        ()=>{
                            themeToggle.toggleColorMode()
                        }
                    }/>
                </Box>

            </Box>
        </>
    )
}
