// technical guide 
//https://www.pluralsight.com/guides/how-to-use-a-simple-form-submit-with-files-in-react

import React from "react";
export let fileRef;

export default function Fileuploader() {
    fileRef = React.useRef (null);

    return (
        <div>
            <input 
            type="file"
            ref={fileRef}
            style = { { display:'none' } }
            />
        </div>
    )
}
