export let fileRef;

export default function Fileuploader() {
    fileRef = React.useRef ();

    let handleChange = (e) => {
        if (e.target.files && e.target.files[0]) {
            let file = e.target.files[0];

        }
    }

    return (
        <div>
            <input 
            type="file"
            ref={fileRef}
            style = { { display:'none' } }
            onChange = {handleChange}
            />
        </div>
    )
}
